mapper.select:
select m.years, m.highestScore, m.minimumScore, m.average, m.highestRank, m.avgRank, m.minimumRank, case
when m.batch_code in('11' ,'10', '1', '2') then hdcl.science_key_line when m.batch_code in('12' ,'3', '4') then hdcl.science_undergraduate_line when m.batch_code not in('11' ,'10', '1', '2', '12' ,'3', '4') then hdcl.science_specialty_line
when m.batch_code in('11' ,'10', '1', '2') then hdcl.liberal_arts_key_line when m.batch_code in('12' ,'3', '4') then hdcl.liberal_arts_undergraduate_line when m.batch_code not in('11' ,'10', '1', '2', '12' ,'3', '4') then hdcl.liberal_arts_specialty_line
else '' end controlLine from (select t.years as years, max(t.highest_score) as highestScore, min(t.minimum_score) as minimumScore, avg(t.average) as average, max(t.highest_rank) as highestRank, avg(t.avg_rank) as avgRank, min(t.minimum_rank) as minimumRank, t.batch_code as batch_code from history_admission_data t where t.educational_code = #{educationalCode}
and t.major_id = #{majorId}
and t.remark like #{remarkOption}
and t.batch_code = #{batchCode}
group by t.years) m, history_data_control_line hdcl where m.years = hdcl.year
